import { useState, useEffect } from "react";
import { Container, Row } from "react-bootstrap";
import {ArrowRightCircle} from "react-bootstrap-icons";
import HeaderImage from "../assets/img/header-img.svg";
import TrackVisibility from 'react-on-screen';
 function AppBanner (){
    const [loopNum, setLoopNum] = useState(0);
    const [isDeleting, setIsDeleting] = useState(false);
    const [text, setText] = useState('');
    const [delta, setDelta] = useState(300 - Math.random() * 100);
    const [index, setIndex] = useState(1);
    const toRotate = [ "Flutter", "Node js", "React" ];
    const period = 2000;


    useEffect(()=>{
        let ticket = setInterval(()=>{
            tick();
        }, delta) 
        return ()=>{ clearInterval(ticket)};
    },[text])
    const tick =()=>{
        let i = loopNum % toRotate.length;
        let fullText = toRotate[i];
        let updatedText = isDeleting ? fullText.substring(0, text.length - 1): fullText.substring(0, text.length + 1);
        setText(updatedText);

    if (isDeleting) {
        setDelta(prevDelta => prevDelta / 2);
      }
      if (!isDeleting && updatedText === fullText) {
        setIsDeleting(true);
        setIndex(prevIndex => prevIndex - 1);
        setDelta(period);
      } else if (isDeleting && updatedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
        setIndex(1);
        setDelta(500);
      } else {
        setIndex(prevIndex => prevIndex + 1);
      }
    }

    return(

            <section className="banner" id="home">
                <Container>
                    <Row className="align-items-center">
                        <col xs={12} md={6}xl={7}>
                            <TrackVisibility>
                            <span className="tagline">Welcome to my portafolio</span>
                            <h1>{"I am a Software Developer"}<span className="warp">text</span></h1>
                           <p>Software Developer skilled in crafting mobile applications, effective problem solver, committed to clean coding, design patterns, and seamless user experiences. </p>
                            <button>onClick={()=> console.log("conect")}Let's connect <ArrowRightCircle size = {25}> </ArrowRightCircle></button>
                            </TrackVisibility>
                        </col>

                        <col xs={12} md={6}xl={7}>
                            <img src={HeaderImage} alt="Header Img"> </img>
                        </col>
                    </Row>
                </Container>
            </section>

    )
 }
export default AppBanner;
